mapper.select:
SELECT * FROM ( SELECT /*+INDEX_DESC(t_review SYS_C0012802) */ ROWNUM rn, reviewNo, category, title, id, regdate, viewCnt FROM t_review WHERE
(id = #{id}) AND
rownum <= #{pageNum} * #{amount} ) WHERE rn > (#{pageNum} -1) * #{amount}

mapper.update:
UPDATE 	t_review
SET		title = #{title}, content = #{content}, 
		category = #{category}, grade = #{grade}
WHERE 	reviewNo = #{reviewNo}

UPDATE 	t_ticket_buy
SET		review = 1
WHERE 	ticketNo = #{ticketNo}

UPDATE t_review
SET viewCnt = viewCnt + 1
WHERE reviewNo = #{reviewNo}
